import React from 'react'
import style from './a.module.css'
import poster from "../../assets/poster.png";
import ResultCard from '../ResultCard/ResultCard';

const data = {
    title: "Rahul Sharma",
    rank: "JEE AIR rank 2",
    details:
        "When an unknown printer took a galley of type and scrambled it to make a type specimen book.",
    src: poster,
}

const scrollContainer = useRef(null);

  useEffect(() => {
    scrollContainer.current.scrollTo(0, scrollContainer.current.scrollHeight);
  }, []);

const HomeBulleton = () => {
    return (
        <div className={style.HomeBulleton}>
            <div className={style.container}>
                <div className={style.left} ref={scrollContainer}>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                    <div className={style.bullet}>Lorem ipsum dolor sit amet.</div>
                </div>
                <div className={style.right}>
                    <ResultCard title={data.title} rank={data.rank} details={data.details} src={data.src} />
                </div>
            </div>
        </div>
    )
}

export default HomeBulleton
